void main() {                                                                                                                                                                                                      
  int b = 3;                                                                                                                                                                                                       
  unsigned int j=0;                                                                                                                                                                                                
  while (j<1 && b!=3) {                                                                                                                                                                                            
    j++;                                                                                                                                                                                                           
  }                                                                                                                                                                                                                
  assert(j<1);                                                                                                                                                                                                     
} 
